import "./components/Expenses/ExpenseItem.css";
import "./components/Expenses/Expenses.css";
import ExpenseArray from "./components/Expenses/ExpenseArray";
import Card from "./components/UI/Card";
import NewExpense from "./components/NewExpense/NewExpense";

function App() {
  const expenses = [
    {id: "e1",title: "Toilet Paper",amount: 94.12,date: new Date(2020, 7, 14)},
    {id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12)},
    {id: "e3",title: "Car Insurance",amount: 294.67,date: new Date(2021, 2, 28)},
    {id: "e4",title: "New Desk (Wooden)",amount: 450,date: new Date(2021, 5, 12)},
  ];

  function addExpenseHandler(enteredExpenseData){
    const appLevelDataObj = {
      ...enteredExpenseData,
      appLevelId: Math.random().toString()
    };
    console.log('this was logged from the app level' + appLevelDataObj.toString())
  }

  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler}/>
      <Card className="expenses">
        <ExpenseArray args={expenses} />
      </Card>
    </div>
  );
}

export default App;
