import "./ExpenseForm.css";
import React, { useState } from 'react';

function ExpenseForm() {
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');
    
    function addExspenseHandler(){ console.log('INFO add expense form submitted');}
    function titleChangeHandler(event){
        setEnteredTitle(event.target.value);
    }

    function amountChangeHandler(event){
        setEnteredAmount(event.target.value);
        console.log('INFO amount in form updated' + event.target.value)
    }
    function dateChangeHandler(event){
        setEnteredAmount(event.target.value);
        console.log('INFO date in form updated' + event.target.value)
    }
    function submitHandler(){
        
    }

  return( 
    <form onSubmit={submitHandler}>
        <div className="new-expense__controls">
            <div className="new-expense__control">
                <label>Title</label>
                <input  type="text" onChange={titleChangeHandler}/>
            </div>
            <div className="new-expense__control">
                <label>Amount</label>
                <input type="number" min='0.00' step="0.01" onChange={amountChangeHandler}/>
            </div>
            <div className="new-expense__control">
                <label>Date</label>
                <input type="date" min='2010-01-01' max='2022-12-31' onChange={dateChangeHandler}/>
            </div>
        </div>
        <div className='new-expense__actions'>
            <button type='submit' onClick={addExspenseHandler}>Add Expense</button>
        </div>
    </form>
    );
}

export default ExpenseForm;
