import React from 'react';
import './Chart.css';
import ChartBar from './ChartBar';

function Chart(props) {
  const totalMaximum = Math.max(...(props.totalByMonth.map(aDataPoint => aDataPoint.value))); 
  
  return( 
  <div className='chart'>
      {props.totalByMonth.map(dataPoint => <ChartBar key={dataPoint.label} value={dataPoint.value} maxValue={totalMaximum} label={dataPoint.label}/>)}
  </div>);
};

export default Chart;
