import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter";
import "./ExpenseItem.css";

function ExpenseArray(props) {
  const [expenseYear, setExpenseYear] = useState("2020");

  function yearSelectHandler(newValue) {
    setExpenseYear(newValue);
    console.log("expenseArray passing the State: " + expenseYear.toString());
  }

  const filteredExpenses = props.args.filter((expense) => {
    return expense.date.getFullYear().toString() === expenseYear
  });

  let expenseContent = <p className='expense-item'>No Expenses Found</p>;

  if (filteredExpenses.lenget > 0){
    expenseContent = filteredExpenses.length > 0 && filteredExpenses.map(
      (expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      )
    )
  }

  return (
    <div>
      <ExpensesFilter selected={expenseYear} onYearSelect={yearSelectHandler} />
      {expenseContent}
    </div>
  );
}

export default ExpenseArray;
