import React from 'react';
import './Chart.css';
import ChartBar from './ChartBar';

function Chart(props) {
  console.log('class \'Chart\' logs start here:')  
  
  const dataPointValues = props.dataPoints.map(dataPoint => dataPoint.value);
  const totalMaximum = Math.max(...dataPointValues);
  
    console.log(props.totalByMonth.value)
  return( 
  <div className='chart'>
      {props.totalByMonth.map(dataPoint => <ChartBar key={dataPoint.label} value={dataPoint.value} maxValue={totalMaximum} label={dataPoint.label}/>)}
  </div>);
};

export default Chart;
