import React, { useState } from "react";
import "./components/Expenses/ExpenseItem.css";
import "./components/Expenses/Expenses.css";
import ExpenseArray from "./components/Expenses/ExpenseArray";
import Card from "./components/UI/Card";
import NewExpense from "./components/NewExpense/NewExpense";
import ToggleNewExpense from "./components/toggleNewExpense/ToggleNewExpense";
import "./components/NewExpense/NewExpense.css";

const dummy_expenses = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

function App() {
  const [expenses, setExpenses] = useState(dummy_expenses);
  const [editing, setEditing] = useState(false);

  function addExpenseHandler(expense) {
    setExpenses((prevExpenses) => {
      return [expense, ...prevExpenses];
    });
    // console.log('\nthis was logged from the app level')
    // console.log(expenses)
  }

  let expenseForm = false;

  function setEditingFalse() {
    setEditing(false);
  }
  function setEditingTrue() {
    setEditing(true);
  }

  let content2 = <h3>button pressed</h3>;
  let content = (
    <div className="new-expense">
      <h3>{expenseForm.toString()}</h3>
      <button onClick={toggleForm}>Cancel</button>
    </div>
  );

  return (
    <div>
      <ToggleNewExpense />
      <div className="new-expense">
        {editing && <div><h2>form goes here</h2><button onClick='setEditingFalse'>toggle</button></div>}
        {!editing && <div><h2>no form here</h2><button onClick='setEditingTrue'>toggle</button></div>}
      </div>


      <NewExpense onAddExpense={addExpenseHandler} />
      <Card className="expenses">
        <ExpenseArray args={expenses} />
      </Card>
    </div>
  );
}

export default App;
