import "./ExpenseForm.css";
import React, { useState } from "react";

function ExpenseForm(props) {
  const [enteredTitle, setEnteredTitle] = useState('');
  const [enteredAmount, setEnteredAmount] = useState('');
  const [enteredDate, setEnteredDate] = useState('');


  function titleChangeHandler(event) {
    setEnteredTitle(event.target.value);
    console.log("INFO title in form updated: " + event.target.value);
  }

  function amountChangeHandler(event) {
    setEnteredAmount(event.target.value);
    console.log("INFO amount in form updated: " + event.target.value);
  }
  function dateChangeHandler(event) {
    setEnteredDate(event.target.value);
    console.log("INFO date in form updated: " + event.target.value);
  }
  function submitHandler(event) {
    event.preventDefault();
    const expenseData = {
        title:enteredTitle,
        amount:enteredAmount,
        date: new Date(enteredDate)
    }

    props.onSaveExpenseData(expenseData);
    console.log('INFO logging expense data from inside expense form: '+ expenseData);
    setEnteredTitle('');
    setEnteredAmount('');
    setEnteredDate('');
  }

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" vlaue={enteredTitle} onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.00" step="0.01" onChange={amountChangeHandler} vlaue={enteredAmount}/>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" min="2010-01-01" max="2022-12-31" onChange={dateChangeHandler} vlaue={enteredDate}/>
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">
          Add Expense
        </button>
      </div>
    </form>
  );
}

export default ExpenseForm;
