import "../NewExpense/NewExpense.css"
function ToggleNewExpense () {
     
  let expenseForm = false;

  const toggleForm = () => expenseForm = !expenseForm;  
  
  return(<div className="new-expense__controls">
        <button onClick={toggleForm}>Add New Expense</button></div>)
}
export default ToggleNewExpense;
