import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter";

function ExpenseArray(props) {
  const [expenseYear, setExpenseYear] = useState("2020");

  function yearSelectHandler(newValue) {
    setExpenseYear(newValue);
    console.log("expenseArray passing the State: " + expenseYear.toString());
  }

  return (
    <div>
      <ExpensesFilter selected={expenseYear} onYearSelect={yearSelectHandler} />
      <Card>
        {props.args.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}

        {/* <ExpenseItem
          title={props.args[0].title}
          amount={props.args[0].amount}
          date={props.args[0].date}
        />
        <ExpenseItem
          title={props.args[1].title}
          amount={props.args[1].amount}
          date={props.args[1].date}
        />
        <ExpenseItem
          title={props.args[2].title}
          amount={props.args[2].amount}
          date={props.args[2].date}
        />
        <ExpenseItem
          title={props.args[3].title}
          amount={props.args[3].amount}
          date={props.args[3].date}
        /> */}
      </Card>
    </div>
  );
}

export default ExpenseArray;
