import React, { useState } from 'react';

import './ExpensesFilter.css';

const ExpensesFilter = (props) => {

    const [year, setYear] = useState('2020');
    let year2 = '';

    function dorpdownChangeHandler(event){
        setYear(event.target.value.toString());
        year2 = event.target.value.toString();
        logSomeStuff(event.target.value)
        props.onYearSelect(event.target.value);
    }

    const logSomeStuff = (value) =>{
        console.log('value obtained via dropdown: ' + value.toString());
        console.log('expense filter state: ' + year.toString());
        console.log('expense filter local var: ' + year2.toString());
    }

  return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select value={props.selected} onChange={dorpdownChangeHandler}>
          <option value='2022'>2022</option>
          <option value='2021'>2021</option>
          <option value='2020'>2020</option>
          <option value='2019'>2019</option>
        </select>
      </div>
      <h1>{year}</h1>
    </div>
  );
};

export default ExpensesFilter;
