import React, { Fragment } from "react";
import ReactDOM from "react-dom";

import Button from "./Button";
import Card from "./Card";
import classes from "../UI/ErrorModal.module.css";

const innerReset = props => {
  console.log('inner reset reached');
  //props.reset;
}

const Backdrop = (props) => {
  // return <div/>;
  console.log('backdrop component reached.')
  return <div className={classes.backdrop} onConfirm={props.onConfirm} />;
};

const ModalOverlay = (props) => {
  // return <div />;
 
 return (<Card className={classes.modal}>
        <header className={classes.header}>
          <h2>{props.title}</h2>
        </header>
        <div className={classes.content}>
          <p>{props.message}</p>
        </div>
        <footer className={classes.actions}>
          <Button onClick={props.reset}>Okay</Button>
        </footer>
      </Card>);
};

const buttonTest = () => {
  console.log("FIRE!!!!!!!!!!!");
};

const ErrorModal = (props) => {
  return (
    <Fragment>
      {/* <div className={classes.backdrop} onClick={props.onConfirm} /> */}
      {/* <Card className={classes.modal}>
        <header className={classes.header}>
          <h2>{props.title}</h2>
        </header>
        <div className={classes.content}>
          <p>{props.message}</p>
        </div>
        <footer className={classes.actions}>
          <Button onClick={props.onConfirm}>Okay</Button>
        </footer>
      </Card> */}

      {ReactDOM.createPortal(
        <Backdrop onClick={props.onClick} />,
        document.getElementById("backdrop-root")
      )}

      {ReactDOM.createPortal(
        <ModalOverlay
          title={props.title}
          message={props.message}
          onClick={buttonTest}
        />,
        document.getElementById("overlay-root")
      )}

    </Fragment>
  );
};

export default ErrorModal;

