import React, { useState, useRef } from "react";
import Card from "../UI/Card";
import classes from "./AddUser.module.css";
import Button from "../UI/Button";
import ErrorModal from "../UI/ErrorModal";
import Wrapper from "../Helpers/Wrapper"

const AddUser = (props) =>{

  const nameInputRef = useRef();
  const ageInputRef = useRef();

  const [error, setError] = useState();
  const [input, setInput] = useState();

  const getInputs = (uName, uAge) => {
    setInput({
      name: nameInputRef.current.value, 
      age: ageInputRef.current.value
    });
    console.log(input[0]);
    return;
  }

  const saveAndReset = x => {
    props.onAddUser(input[0], input[1]);
    nameInputRef.current.value = '';
    ageInputRef.current.value = '';
  }

  const checkValidity = (input) => {
    console.log(input);
    if (input[0].trim().length === 0) {
        setError({
          title: "no name found",
          message: "please enter a name",
        });
        return;
      }
    if(input[1].trim().length === 0){
      setError({
        title: "no age found",
        message: "please enter an age",
      });
      return;
    }
    if(+input[1]<1){
      setError({
        title: "invalid age entered",
        message: "please enter a positive int",
      });
      return;
    }
    setError(null);
  }

  const addUserHandler = (event) => {
    event.preventDefault();
    const input = getInputs(nameInputRef, ageInputRef);
    checkValidity(input) 
    if(!error){
      saveAndReset();
    }
  };

  const errorHandler = () => {
    setError(null);
  };

  return (
    <Wrapper>
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          reset={errorHandler}
        />
      )}
      <Card className={classes.input}>
        <form onSubmit={addUserHandler}>
          <label htmlFor="username">Username</label>
          <input
            id="username"
            type="text"
            ref={nameInputRef}
          />
          <div />
          <label htmlFor="age">Age [years(integer)]</label>
          <input
            id="age"
            type="number"
            ref={ageInputRef}
          />
          <Button type="submit">Add User 2</Button>
        </form>
      </Card>
    </Wrapper>
  );
}

export default AddUser;
