import React, { useState, useRef } from "react";
import Card from "../UI/Card";
import classes from "./AddUser.module.css";
import Button from "../UI/Button";
import ErrorModal from "../UI/ErrorModal";
import Wrapper from "../Helpers/Wrapper"

const AddUser3 = (props) =>{

  const nameInputRef = useRef();
  const ageInputRef = useRef();

  const [error, setError] = useState();
  const [input, setInput] = useState();

  const getInputs = () => {
    console.log(nameInputRef.current.value);      
      setInput({
      name: nameInputRef.current.value, 
      age: ageInputRef.current.value
    });
    console.log(input.name);
    console.log(input.age);
    return;
  }

  const saveAndReset = x => {
    props.onAddUser(input.name, input.age);
    nameInputRef.current.value = '';
    console.log('name value after reset: '+ nameInputRef.current.value);
    ageInputRef.current.value = '';
    console.log('age value after reset: '+ ageInputRef.current.value);
  }

  const checkValidity = (input) => {
    // console.log(input.name);
    if (input.name.trim().length === 0) { 
        setError({
          title: "no name found",
          message: "please enter a name",
        });
        return;
    }
    if(input.age.trim().length === 0){
      setError({
        title: "no age found",
        message: "please enter an age",
      });
      return;
    }
    if(+input.age<1){
      setError({
        title: "invalid age entered",
        message: "please enter a positive int",
      });
      return;
    }
    setError(null);
  }

  const addUserHandler = (event) => {
    event.preventDefault();
    console.log('default button action prevented, getting getInputs.');

    getInputs();
    console.log('checking validity of inputs');
    checkValidity(input) 
    console.log('checking error flag');
    if(!error){
      saveAndReset();
    }
  };

  const errorHandler = () => {
    setError(null);
  };

  return (
    <Wrapper>
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          reset={errorHandler}
        />
      )}
      <Card className={classes.input}>
        <form onSubmit={addUserHandler}>
          <label htmlFor="username">Username</label>
          <input
            id="username"
            type="text"
            ref={nameInputRef}
          />
          <div />
          <label htmlFor="age">Age [years(integer)]</label>
          <input
            id="age"
            type="number"
            ref={ageInputRef}
          />
          <Button type="submit">Add User (using factoring)</Button>
        </form>
      </Card>
    </Wrapper>
  );
}

export default AddUser3;
