import React, {
  /*useReducer,  useRef, useEffect, useContext, useImperativeahandle, Fragment, */useState, 
} from "react";
import Header from "./components/Layout/Header/Header";
import Meals from "./components/Meals/Meals";
import Cart from './components/Cart/Cart';
import CartProvider from "./store/CartProvider";
import Button from './components/UI/Button/Button'

let showAddMeals = false;

const postMeals = () => {
  fetch()
}

function App() {
  const [cartIsShown, setCartIsShown] = useState(false);

  const togleCartIsShownHandler = () => {
    console.log('toggle fun reached')
    if (cartIsShown){
      setCartIsShown(false);
      return;
    }
    setCartIsShown(true);
  }
  return (
    <CartProvider>
      {cartIsShown && <Cart closeCart={togleCartIsShownHandler}/>}
      <Header onButtonClick={togleCartIsShownHandler}/>
      <main>
        <Meals />
      </main>
      <Button onClick={showAddMeals = !showAddMeals}>Add Meals</Button>
      {showAddMeal && <AddMeal closeForm={showAddMeals = false}/>}
    </CartProvider>
  );
}

export default App;
