import Card from "../../UI/Card/Card";
import MealItem from "../MealItem/MealItem";
import classes from "./AvailableMeals.module.css";
import DUMMY_MEALS from "../../../assets/DUMMY_MEALS ";
import useHTTP from "../../../hooks/useHTTP";
import { useCallback, useState } from "react";
import URLs from "../../../assets/URLs";

const AvailableMeals = () => {
  // const request = useHTTP();
  
  const [isLoading, setIsLoading] = useState(false);
  const [hasError, setHasError] = useState(false);
  
  const getMeals = useCallback(async () => {
    setIsLoading(true);
    setHasError(false);
    const availableMeals = [];

    try {
      const responce = await fetch(URLs.meals);

      // console.log('printing \'responce\': ');
      // console.log(responce);

      if (!responce.ok) {
        throw new Error("failed to get available meals");
      }

      const data = await responce.json();

      for (const mealKey in data) {
        availableMeals.push({
          id: mealKey,
          name: data[mealKey].name,
          description: data[mealKey].description,
          price: data[mealKey].price,
        });
      }
    } catch (e) {
      setHasError(
        e.message || "there was an error, but failed to collect message"
      );
    }

    console.log(availableMeals);
    setIsLoading(false);
    return availableMeals;
  }, []);

  const mealsList = DUMMY_MEALS.map((x) => {
    return <MealItem key={x.id} data={x} />;
  });
  // const mealsList = []

  return (
    <Card className={classes.meals}>
      <ul>{mealsList}</ul>
    </Card>
  );
};

export default AvailableMeals;
