import React, {
  /*useReducer,  useRef, useEffect, useContext, useImperativeahandle, */useState, Fragment,
} from "react";
import Header from "./components/Layout/Header/Header";
import Meals from "./components/Meals/Meals";
import Cart from './components/Cart/Cart';


function App() {
  const [cartIsShown, setCartIsShown] = useState(false);

  const togleCartIsShownHandler = () => {
    console.log('toggle fun reached')
    if (cartIsShown){
      setCartIsShown(false);
      return;
    }
    setCartIsShown(true);
  }
  return (
    <Fragment>
      {/* {cartIsShown && <Cart />} */}
      <Header onShowCart={togleCartIsShownHandler}/>
      <main>
        <Meals />
      </main>
    </Fragment>
  );
}

export default App;
