import { Fragment } from 'react';
import ReactDOM from 'react-dom';

import classes from './Modal.module.css';

const Backdrop = (props) => {
  return <div className={classes.backdrop} onClick={props.onClick}/>;
};

const ModalOverlay = (props) => {
  return (
    <div className={classes.modal}>
      <div className={classes.content}>{props.children} this text is coming from the modal overlay, but I don't know why 'onClick=' appears</div>
    </div>
  );
};

//const portalElement = document.getElementById('overlays');

const Modal = (props) => {
  return (
    <Fragment>
      {ReactDOM.createPortal(<Backdrop onClick={props.closeCart}/>, document.getElementById('overlays'))}
      {ReactDOM.createPortal(<ModalOverlay>{props.children} onClick={props.onClick}</ModalOverlay>, document.getElementById('overlays'))}
    </Fragment>
  );
};

export default Modal;
