import { useState } from "react";
import URLs from "../assets/URLs";

const useHTTP = () =>{
    const [isLoading, setIsLoading] = useState(false);
    const [hasError, setHasError] = useState(false);

    const sendMeal = async (data) => {
       try{
        const responce = await fetch(URLs.meals, {
            method: 'POST',
            headers: { 
                "Content-Type": "application/json" 
              },
            body: JSON.stringify(data),
        })
        if(!responce.ok){ throw new Error('failed to add meal') }
        } catch (e) { setHasError(e.message || 'there was an error, but failed to collect message'); }
    }
    
    const getMeals = async () => {
        const availableMeals = []

        try{
            const responce = await fetch(URLs.meals);
            
            console.log('printing \'responce\': ');
            console.log(responce);

            if(!responce.ok){ throw new Error('failed to get available meals') }

            const data = await responce.json();
            console.log('printing \'data\': ');
            console.log(data);
            console.log('printing \'availableMeals\': ');
            console.log(availableMeals);
            availableMeals.push({name: data.MuRlLDnBc_9XwRXvXJ0.name})



            for (const mealKey in data) {
                availableMeals.push({
                    id: mealKey,
                    name: data[mealKey].name,
                    description: data[mealKey].description,
                    price: data[mealKey].price
                    })
                }   
        } catch (e) { setHasError(e.message || 'there was an error, but failed to collect message'); }
        return availableMeals;
    }

    const sendOrder = async () => {
       try{
        const responce = await fetch()
        } catch {}
    }    
    
    return{
        isLoading,
        hasError,
        sendMeal, 
        getMeals,
        sendOrder
    }
}; export default useHTTP;
