import React, {  Fragment,  useState /*useReducer , useRef, useEffect, useContext, useImperativeahandle, useMemo,*/,} from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState('false')

  async function fetchMoviesHandler() {
    setIsLoading(true)
    const response = await fetch('https://swapi.dev/api/films/');
    const data = await response.json();
    const transformedMovies = data.results.map((film) => {
      return { 
        id: film.episode_id,
        title: film.title,
        releaseDate: film.release_date,
        openingText: film.opening_crawl 
      };
    });
    setMovies(transformedMovies);
    setIsLoading(false);
  }

  return (
    <Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && <MoviesList movies={movies} />}
        {isLoading && <p>loadin' it!</p>}

      </section>
    </Fragment>
  );
}

export default App;




