import React, { Fragment, useEffect, useState, useCallback /*useReducer , useRef,  useContext, useImperativeahandle, useMemo,*/,
} from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchMoviesHandler = useCallback( async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("https://swapi.dev/api/films/");
      //if (response.status < 200 || response.status > 299){
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();
      const transformedMovies = data.results.map((film) => {
        return {
          id: film.episode_id,
          title: film.title,
          releaseDate: film.release_date,
          openingText: film.opening_crawl,
        };
      });
      setMovies(transformedMovies);
    } catch (anError) {
      setError(anError.message);
    }
    setIsLoading(false);
  }, []);

  useEffect (() => {
    fetchMoviesHandler();
  }, []);

  let content = <p>Nothin' to see here!, Move along!</p>
  if(error){ content = <p>{error}</p> };
  if(movies.length < 1){ content = <p>Nothin' to see here!, Move along!</p> };
  if(!isLoading && movies.length > 0){ content = <MoviesList movies={movies} /> };
  if(isLoading){ content = <p>loadin' it!</p> };

  return (
    <Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {content}
      </section>
    </Fragment>
  );
}

export default App;

