import React, {
  Fragment,
  useState /*useReducer , useRef, useEffect, useContext, useImperativeahandle, useMemo,*/,
} from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  async function fetchMoviesHandler() {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("https://swapi.dev/api/films/");
      //if (response.status < 200 || response.status > 299){
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();
      const transformedMovies = data.results.map((film) => {
        return {
          id: film.episode_id,
          title: film.title,
          releaseDate: film.release_date,
          openingText: film.opening_crawl,
        };
      });
      setMovies(transformedMovies);
    } catch (anError) {
      setError(anError.message);
    }
    setIsLoading(false);
  }
  async function badFetchMoviesHandler() {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("https://swapi.dev/api/film/");
      //if (response.status < 200 || response.status > 299){
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();
      const transformedMovies = data.results.map((film) => {
        return {
          id: film.episode_id,
          title: film.title,
          releaseDate: film.release_date,
          openingText: film.opening_crawl,
        };
      });
      setMovies(transformedMovies);
    } catch (anError) {
      setMovies({})
      setError(anError.message);
    }
    setIsLoading(false);
  }

  return (
    <Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
        <button onClick={badFetchMoviesHandler}>Don't Fetch Movies</button>
      </section>
      <section>
        {!isLoading && movies.length > 0 && <MoviesList movies={movies} />}
        {!isLoading && movies.length < 1 && error === null && <p>Nothin' to see here!, Move along!</p>}
        {isLoading && <p>loadin' it!</p>}
        {!isLoading && error && <p>{error}</p>}
      </section>
    </Fragment>
  );
}

export default App;

