import { useState } from 'react';

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState('');
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(false);
  const [enteredNameTouched, setEnteredanameTouched] = useState(false);

  const nameInputIsInvalid = !enteredNameIsValid && enteredNameTouched;

  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);
    validateInput();
  };

  const validateInput = () => {
    setEnteredanameTouched(true);
    event.target.value.trim() !== '' ? setEnteredNameIsValid(true) : setEnteredNameIsValid(false);
    // if(enteredName.trim() === ''){setEnteredNameIsValid(false); return false;}
    // setEnteredNameIsValid(true); return true; 
  }

  const formSubmissionHandler = event => {
    event.preventDefault();

    validateInput();

    console.log(enteredName);
    setEnteredName('');
  }
  const nameInputClasses = nameInputIsInvalid ? 'form-control invalid' : 'form-control';

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' onChange={nameInputChangeHandler} value={enteredName} onBlur={validateInput}/>
        {nameInputIsInvalid && <p className='error-text'>name must not be blank</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
