import useValidate from "../hooks/useValidate";

const BasicForm = (props) => {
  const {
    inputState: firstState,
    onChange: onNameChange,
    onBlur: onNameBlur,
    reset: resetName
  } = useValidate(x => x.trim() !== '')
//} = useInput(x => x.trim() !== '');

  let formIsValid = firstState.IsValid //TODO: add other inputs
  firstIsValid //&& lastIsValid && emailIsValid;
  
  const buttonClassString = formIsValid ? 'form-actions' : 'form-actions invalid'
  const firstClassString = firstState.IsValid ? 'form-actions' : 'form-actions invalid'
  // const lastClassString = lastIsValid ? 'form-actions' : 'form-actions invalid'
  // const emailClassString = emailIsValid ? 'form-actions' : 'form-actions invalid'

  return (
    <form>
      <div className='control-group'>
        <div className={firstClassString}>
          <label htmlFor='name'>First Name</label>
          <input value={firstState.value} type='text' id='name' />
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div>
      <div className={buttonClassString}>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
