import useValidate from "../hooks/useValidate";

const BasicForm = (props) => {
  const {
    inputState: firstState,
    onChange: onFirstChange,
    onBlur: onFirstBlur,
    reset: resetFirst
  } = useValidate(x => x.trim() !== '')
//} = useInput(x => x.trim() !== '');

  let formIsValid = false;
  if(firstState.isValid){
    formIsValid = true;
  }
  
  const firstClassString = firstState.hasError 
  ? 'form-control invalid' : 'form-control'
  // const lastClassString = lastIsValid ? 'form-control' : 'form-actions invalid'
  // const emailClassString = emailIsValid ? 'form-control' : 'form-actions invalid'

  return (
    <form>
      <div className='control-group'>
        <div className={firstClassString}>
          <label htmlFor='name'>First Name</label>
          <input onChange={onFirstChange} onBlur={onFirstBlur} value={firstState.value} type='text' id='name' />
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div>
      <div className='form-actions'>
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
