import { Component } from 'react';
import { useSelector, useDispatch, connect }from 'react-redux'

import classes from './Counter.module.css';

const Counter = () => {
  const counter = useSelector(state => state.counter);
  const dispatch = useDispatch();

  const incHandler = () => {
    dispatch( { type: 'INCREMENT', })
  }

  const decHandler = () => {
    dispatch( { type: 'DECREMENT', })
  }
   
  const toggleCounterHandler = () => {};

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>{counter}</div>
      <div>
        <button onClick={incHandler}>inc</button>
        <button onClick={decHandler}>dec</button>
      </div>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

class Conter extends Component {
  incHandler() { this.props.increment(); }

  decHandler() { this.props.increment(); }

  toggleCounterHandler() {}

  render() {
    return(
      <main className={classes.counter}>
        <h1>Redux Counter</h1>
        <div className={classes.value}>{this.props.counter}</div>
        <div>
          <button onClick={this.incHandler.bind()}>inc</button>
          <button onClick={this.decHandler.bind()}>dec</button>
        </div>
        <button onClick={this.toggleCounterHandler}>Toggle Counter</button>
      </main>
    )
  }
}

const mapStateToProps = state => {
  return {
    counter: state.counter
  }
}

const mapDispatchToProps = dispatch => {
  return {
    increment: () => dispatch({type: 'INCREMENT'}),
    decrement: () => dispatch({type: 'DECREMENT'}),
  }
}


export default connect(mapStateToProps, )(Counter);
