import { createStore } from "redux";

const initialState = {
  counter: 0,
};

const counterReducer = (state = initialState, action) => {
  //this is our reducer, it manages the state
  if (action.type === "INCREMENT") {
    let newVal = state.counter + 1;
    for (var i = 0; i < action.times - 1; i++) {
      newVal++;
    }
    return { counter: newVal };
  }

  if (action.type === 'INCREASE') {
      console.log(state.counter.toString() + action.amount.toString());
      console.log(state.counter + action.amount);
      return { counter: (state.counter + action.amount) }
  }

  if (action.type === "DECREMENT") {
    return { counter: state.counter - 1 };
  }
  return state;
};

const store = createStore(counterReducer);

export default store;
